    private void BTNcalcActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BTNcalcActionPerformed
        // TODO add your handling code here:
        Double total = 0.0;
        Double valor = Double.valueOf(tfValor.getText());
        Integer cod = Integer.valueOf(tfCod.getText());
        switch(cod){
            case 1:
                total = valor * 0.95;
                break;
            case 2:
                total = valor * 0.90;
                
                break;
            case 3:
                total = valor * 0.80;
                break;
            case 4:
                total = valor * 0.50;
                break;
            default:
                taResul.setText("CÃ³digo Invalido");
                break;
        }
        if(cod > 0 && cod <=4){
            String msg = String.format("Valor sem desconto : R$ %.2f \n "
                    + "Valor com desconto : R$ %.2f",valor,total);
            taResul.setText(msg);
            
        }
    }//GEN-LAST:event_BTNcalcActionPerformed

